import { Dashboard } from "../../icons/Dashboard";
import { User } from "../../icons/User";
import { Achievement } from "../../icons/Achievement";
import { Innovation } from "../../icons/Innovation";
import { Report } from "../../icons/Report";
import { Setting } from "../../icons/Setting";
import { Notification } from "../../icons/Notification";
import { Logout } from "../../icons/Logout";

type Props = {};

export const Sidebar = (props: Props) => {
  const nav = [
    {
      icon: <Dashboard />,
      name: "Dashboard",
    },
    {
      icon: <User />,
      name: "User",
    },
    {
      icon: <Achievement />,
      name: "Achievement",
    },
    {
      icon: <Innovation />,
      name: "Innovation",
    },
    {
      icon: <Report />,
      name: "Report",
    },
  ];

  return (
    <>
      <div className="w-64 absolute sm:relative bg-gray-900 shadow  flex-col justify-between flex sm:flex h-screen ">
        <div>
          {/* <div className="h-16 w-full flex items-center px-8">
                        logo
                    </div> */}

          <ul className="mt-5">
            {nav?.map((item) => {
              return (
                <li className="flex w-full justify-between text-gray-600 hover:text-gray-300 hover:bg-indigo-800 cursor-pointer items-center py-3 px-8">
                  <div className="flex items-center">
                    {item.icon}
                    <span className="text-sm  ml-2">{item.name}</span>
                  </div>
                </li>
              );
            })}
          </ul>

          {/* profile */}
          <div className="flex items-center mb-4 px-8 absolute bottom-10 ">
            <div className="w-10 h-10 bg-cover rounded-md mr-3">
              <img
                src="https://tuk-cdn.s3.amazonaws.com/assets/components/avatars/a_5.png"
                alt="profile"
                className="rounded-full h-full w-full overflow-hidden shadow"
              />
            </div>
            <div>
              <p className="text-gray-600 text-sm font-medium">Steve Doe</p>
              <p className="text-gray-600 text-xs">View Profile</p>
            </div>
          </div>
        </div>
        <div className="px-8 bg-gray-900">
          <ul className="w-full flex items-center justify-between ">
            {[<Notification />, <User />, <Setting />, <Logout />].map(
              (item) => {
                return (
                  <li className="cursor-pointer text-white pt-5 pb-3">
                    {item}
                  </li>
                );
              }
            )}
          </ul>
        </div>
      </div>
      <div className="text-gray-600 visible sm:hidden relative">
        {/* <ul className="p-y w-64 border-r bg-indigo-900 absolute rounded left-0 shadow mt-8 sm:mt-16 hidden">
          {nav?.map((item) => {
            return (
              <li className="flex w-full justify-between text-gray-600 hover:text-gray-300 hover:bg-indigo-800 cursor-pointer items-center py-3 px-2">
                <div className="flex items-center">
                  {item.icon}
                  <span className="text-sm  ml-2">{item.name}</span>
                </div>
              </li>
            );
          })}
        </ul> */}
        <svg
          aria-label="Main Menu"
          aria-haspopup="true"
          xmlns="http://www.w3.org/2000/svg"
          className="icon icon-tabler icon-tabler-menu cursor-pointer"
          width={30}
          height={30}
          viewBox="0 0 24 24"
          strokeWidth="1.5"
          stroke="currentColor"
          fill="none"
          strokeLinecap="round"
          strokeLinejoin="round"
        >
          <path stroke="none" d="M0 0h24v24H0z" />
          <line x1={4} y1={8} x2={20} y2={8} />
          <line x1={4} y1={16} x2={20} y2={16} />
        </svg>
      </div>
    </>
  );
};
