import React from 'react'

type Props = {
    status: string,
    message: string
}

const Toast = (props: Props) => {
  return (
    <div className=' flex w-[300px] justify-around px-6 py-3'>
        <p>{props.message}</p>
        <span>{props.status === 'success' ? '✔️' : '❌'}</span>
    </div>
  )
}

export default Toast
