import React, { useState } from "react";
import { Input } from "../../components/Input";
import { Page, Text, View, Document, StyleSheet } from "@react-pdf/renderer";

type Props = {};

const Report = (props: Props) => {
  const [title, setTitle] = useState<string>("");

  const styles = StyleSheet.create({
    page: {
      flexDirection: "row",
      backgroundColor: "#E4E4E4",
    },
    section: {
      margin: 10,
      padding: 10,
      flexGrow: 1,
    },
  });

  return (
    <div className="mx-5 lg:grid grid-cols-2 lg:mx-10 ">
      <section className="mt-10">
        {/* heading */}
        <h1 className="text-center font-medium">Report Maker</h1>

        <div className="mt-10">
          <section className="w-full my-5">
            <p className="my-1 text-sm text-gray-400 ml-1">Title</p>
            <Input
              type="text"
              placeholder="Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
          </section>

          <section className="w-full my-5">
            <p className="my-1 text-sm text-gray-400 ml-1">Date</p>
            <input
              type="date"
              className="bg-blue-50 px-3 py-3 rounded-lg outline-none w-full"
            />
          </section>

          <section className="w-full my-5">
            <p className="my-1 text-sm text-gray-400 ml-1">Content</p>
            <textarea
              cols={30}
              rows={13}
              className="bg-blue-50 px-3 py-3 rounded-lg outline-none w-full"
            />
          </section>
        </div>
      </section>

      <section className="hidden lg:flex flex-col">
        <Document>
          <Page size="A4" style={styles.page}>
            <View style={styles.section}>
              <Text>Section #1</Text>
            </View>
            <View style={styles.section}>
              <Text>Section #2</Text>
            </View>
          </Page>
        </Document>
      </section>
    </div>
  );
};

export default Report;
