import React, { forwardRef } from "react";

type Props = {
  status: string;
  message: string;
};

const Toast = forwardRef((props: Props) => {
  return (
    <div className= {`flex w-[300px] p-3 rounded-lg text-white fixed top-7 inset-x-0  mx-auto items-center justify-center ${props.status === 'success' ? 'bg-green-400' : 'bg-red-400'}`} >
      <p className="mr-5">{props.message}</p>
      <span>{props.status === "success" ? "✔️" : "❌"}</span>
    </div>
  );
});

export default Toast;
